import React, { useEffect, useRef, useState } from 'react';
import * as S from "./style/Tabs1";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot } from '@fortawesome/free-solid-svg-icons';

const Tabs1 = () => {
    const [itemNames, setItemNames] = useState([
        "All", "Design", "Browser", "chatting"
    ]);
    const [seletIndex, setSelectIndex] = useState(0);
    const [selectItem, setSelectItem] = useState("All");
    const [itemList, setItemList] = useState([{
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcStSOD2QoxVXKkpyWSrBTkOR5kxwj0mohW6NA&s",
        title: "사람과 세상을 향한 모든 연결의 시작, 카카오톡",
        name: "Kakao",
        address: "South Korea",
        item: "chatting",
        watch: true
        },{
        img: "data:image/jpeg;base64,",
        title: "오늘 어떤 걸 디자인할까요?",
        name: "Canva",
        address: "New York",
        item: "Design",
        watch: true
       }, {
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIE6vdFu0ogkG3dk0ECW06LEv_QY09pUNT3xkierC4ng&s",
        title: "구글",
        name: "Google",
        address: "New York",
        item: "Browser",
        watch: true
       }, {
        img: "data:image/jpeg;base64,",
        title: "프리미엄 디자인 플랫폼 망고보드",
        name: "Mango board",
        address: "South Korea",
        item: "Design",
        watch: true
       }, {
        img: "data:image/jpeg;base64,",
        title: "세상의 모든 디자인은 미리캔버스로 완성",
        name: "Miri canvas",
        address: "South Korea",
        item: "Design",
        watch: true
       }
    ]);

    const itemNamesRef = useRef<HTMLUListElement | null>(null);
    const itemListRef = useRef<HTMLDivElement>(null);

    // method
    const nameClick = (index: number) => {
        setSelectIndex(index);
        setSelectItem(itemNames[index]);
    }

    useEffect(() => {
        if(itemNamesRef !== null) {
            let names = (itemNamesRef.current as any).children;

            for(let i = 0; i < names.length; i++) {
                names[i].style.backgroundColor = "#eee";
                names[i].style.color = "#444";
            }
            
            let name = names[seletIndex];
            name.style.backgroundColor = "#444";
            name.style.color = "#eee";
        }
    }, [seletIndex]);

    return (
        <S.Container>
            <S.Inner>
                <S.BoardContainer>
                    <S.Title>Company List</S.Title>
                    <S.Description>Find a job you love</S.Description>
                    <S.ItemNames ref={itemNamesRef}>
                    {
                        itemNames.map((item, index) => {
                            return (
                                <S.Name onClick={() => nameClick(index)} key={index}>{item}</S.Name>
                            )
                        })
                    }
                    </S.ItemNames>
                    <S.ItemList>
                    {
                        itemList.map((list, index) => {
                            return (
                                selectItem === "All" || list.item === selectItem ?
                                <S.Item ref={itemListRef} key={index}>
                                    <S.ItemImg src={list.img}/>
                                    <S.ItemContent>
                                        <S.ItemTitle>{list.title}</S.ItemTitle>
                                        <S.ItemName>{list.name}</S.ItemName>
                                        <S.AddressContain>
                                            <FontAwesomeIcon fontSize={13} icon={faLocationDot} color="green" />
                                            <S.ItemAddress>{list.address}</S.ItemAddress>
                                        </S.AddressContain>
                                    </S.ItemContent>
                                </S.Item> :
                                null
                            )
                        })
                    }
                    </S.ItemList>
                </S.BoardContainer>
            </S.Inner>            
        </S.Container>
    );
};

export default Tabs1;